
public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		while(i<=10) {
			System.out.println(i);
			i++; /*without this statement to the left we would have an 
			infinite loop b/c i would never get up to above 10*/
			
		 
			
		}

	}

}
